DROP TABLE IF EXISTS "news_feed";
create table [news_feed] (
[title] nvarchar(5000) not null,
[published_date] datetime not null,
[summary] nvarchar(20000) not null,
[image_src] nvarchar(500) not null,
[main_link] nvarchar(2000) not null,
[category] nvarchar(3000) not null,
[news_src] nvarchar(3000) not null
);

insert into news_feed(title, published_date, summary, image_src, main_link, category, news_src) values ('निहालचंद पर आरोप लगाने वाली महिला की कहानी', '2014-06-22 11:48:21', 'मोदी सरकार में रसायन और उर्वरक राज्यमंत्री निहाल चंद मेघवाल सहित 17 लोगों को दुष्कर्म के एक मामले में अदालत में पेश होने का समन मिलने के बाद राजनीति गरमा गई है। विपक्षी कांग्रेस सहित महिला संगठन लगातार मेघवाल से इस्तीफ़ा मांग रहे हैं।इस मामले में भारतीय जनता पार्टी की राज्य इकाई मेघवाल के समर्थन में खड़ी है और मेघवाल भी हाल ही में अपने संसदीय क्षेत्र के दौरे के दौरान इस्तीफ़ा देने से इनकार कर चुके हैं। आभा शर्मा ने आरोप लगाने वाली महिला से बात की।', 'http://img.amarujala.com/2014/06/22/rajasthan1-53a673ec4a502_exlst.jpg', 'http://www.amarujala.com/feature/states/rajasthan/nihalchand-meghwal-victim-version/','Home', 'Amar Ujala');

insert into news_feed(title, published_date, summary, image_src, main_link, category, news_src) values ('सीक्रेट मीटिंग के बाद मिले रामदेव और PM के सुर', '2014-06-22 11:01:51', 'प्रधानमंत्री नरेंद्र मोदी ने योग गुरु रामदेव के साथ हुई बातचीत में उन सभी मुद्दों पर सहमति जताई है, जो बाबा द्वारा चुनाव के समय उठाए गए थे।अरबों रुपए का कालाधन भारत लाने के मुद्दे को पीएम ने प्राथमिकता से लिया है। प्रधानमंत्री मोदी अगले कुछ महीनों में हरिद्वार आ सकते हैं।उनके निमंत्रण को पीएम ने स्वीकार कर लिया है। यह जानकारी बाबा रामदेव ने दिल्ली से लौटकर दी।', 'http://img.amarujala.com/2014/03/23/modi-ramdev-532ee571dcc6a_exlst.jpg', 'http://www.amarujala.com/feature/dehradun-local/politics-dun/meeting-of-narendra-modi-and-ramdev/','Home', 'Amar Ujala');

insert into news_feed(title, published_date, summary, image_src, main_link, category, news_src) values ('पाकिस्तान में क्यों बढ़ रही हैं हिंसा की घटनाएं?', '2014-06-22 11:31:53', 'क्या भारत में चरमपंथी हिंसा में कमी पाकिस्तान में हिंसा में बढोतरी की कीमत पर आई है? कम से कम पाकिस्तान की खुफ़िया एजेंसी आईएसआई तो ऐसा ही सोचती है।पिछले हफ्ते मैं पाकिस्तान में था और आईएसआई के जिस रिटायर्ड जनरल से मेरी बात हुई, उन्होंने कहा कि पिछले एक दशक के दौरान पाकिस्तान में हिंसा में हुई वृद्धि का एक कारण भारत के खिलाफ सक्रिय चरमपंथी संगठनों को बंद करने का सेना का फैसला भी हो सकता है।वर्ष 2003 में पाकिस्तान में हिंसा बहुत कम थी, जब एक साल में 140 आम लोगों समेत सिर्फ 189 लोग मारे गए थे।इससे एक साल पहले राष्ट्रपति परवेज मुशर्रफ ने लश्कर-ए-तैयबा और जैश-ए-मोहम्मद को प्रतिबंधित कर दिया और फिर भारत में होने वाली हिंसा में कमी आने लगी। पाकिस्तान के मामले में उल्टा हुआ और इसी का जिक्र आईएसआई के रिटायर्ड जनरल कर रहे थे।', 'http://img.amarujala.com/2014/06/14/pakistan-539c4f6d00773_exlst.jpg', 'http://www.amarujala.com/feature/samachar/international/pakistan/why-rising-militant-violence-in-pakistan/','Home', 'Amar Ujala');

insert into news_feed(title, published_date, summary, image_src, main_link, category, news_src) values ('देश तय करे कैसी रेल चाहिए, वर्ल्ड क्लास या खस्ताहाल', '2014-06-22 00:47:00', 'बढ़े रेल किराए और माल भाड़े को लेकर चौतरफा हमलों से घिरी मोदी सरकार के बचाव में वित्त मंत्री अरुण जेटली आगे आए हैं। उन्होंने वर्ल्ड क्लास रेल के नाम पर इस फैसले को जायज ठहराने की कोशिश की है। अपने फेसबुक पेज पर जेटली ने लिखा कि रेल मंत्री ने एक कठिन लेकिन सही फैसला किया है। देश को यह तय करना होगा कि उसे वर्ल्ड क्लास रेल चाहिए या एक खस्ताहाल रेल सेवा।जेटली का कहना है कि भारतीय रेलवे पिछले कुछ साल से घाटे में चल रही है। कई वर्षों से यात्री सेवाओं में हो रहे नुकसान की पूर्ति माल भाड़े की आय से की जा रही थी। लेकिन हाल के वर्षों में माल भाड़े पर भी दबाव बढ़ गया है। रेलवे को बचाने का एक ही तरीका है कि यात्री जिन सुविधाओं का लाभ उठाते हैं उनके लिये भुगतान करें। उनका कहना है कि बेहतर रेल सेवाएं मुहैया कराने के लिए किराए में वृद्धि जरूरी थी।रेल मंत्री के सामने यही विकल्प था कि वह रेलवे को उसकी हालात पर छोड़ दें और इसे कर्ज के जाल में फंसने दे या फिर किराया बढ़ाकर स्थिति संभाली जाए।', 'http://img.amarujala.com/2014/01/28/arun-jaitley-markandey-katju-52e7c985e3734_exlst.jpg', 'http://www.amarujala.com/feature/samachar/national/arun-jaitley-defending-modi-govt-on-rail-fare-hike/','Home', 'Amar Ujala');

insert into news_feed(title, published_date, summary, image_src, main_link, category, news_src) values ('चलो किसी के तो अच्छे दिन आए!', '2014-06-22 00:06:46', 'प्रधानमंत्री नरेंद्र मोदी के एक फरमान से कबाड़ियों के अच्छे दिन आ गए हैं। मोदी के मंत्रालयों में सफाई अभियान के हुक्म से इन दिनों लुटियंस स्थित विभागों से रोजाना कई ट्रक रद्दी निकल रही है। इससे कबाड़ियों की घर बैठे चांदी हो गई। मंत्रालयों में सफाई अभियान के चलते उनका व्यापार एकाएक बढ़ गया है।जिस तरह से सरकारी विभागों से कबाड़ निकल रहा है, उससे लगता है कि कबाड़ियों की अगले एक महीने तक पौ-बारह रहने वाली है। खुले बाजार में रद्दी के दाम 8-10 रुपये प्रति किलो रह गए हैं जोकि कुछ दिनों पहले तक 14 से 15 रुपये किलो चल रहा था।कृषि भवन में सफाई अभियान में लगे एक कर्मचारी ने बताया कि यहां से रोजाना एक ट्रक रद्दी निकल रही है। सफाई अभियान में दस से बारह कर्मचारी लगे हुए हैं।', 'http://img.amarujala.com/2014/06/09/narendra-modi-5394ca2b6be45_exlst.jpg', 'http://www.amarujala.com/feature/samachar/national/tatters-are-in-profit-after-modi-decision/','Home', 'Amar Ujala');

